import { Component } from '@angular/core';
import { Book } from '../interfaces/books';
import { BookAPIService } from '../services/book-api.service';

@Component({
  selector: 'app-m5-post-createbook',
  templateUrl: './m5-post-createbook.component.html',
  styleUrls: ['./m5-post-createbook.component.css'],
})
export class M5PostCreatebookComponent {
  book = new Book();
  books: any;
  errMessage: string = '';
  constructor(private _service: BookAPIService) {
    // this._service.getBooks().subscribe({
    //   next: (data) => {
    //     this.books = data;
    //   },
    //   error: (err) => {
    //     this.errMessage = err;
    //   },
    // });
  }
  postBook() {
    this._service.postBook(this.book).subscribe({
      next: (data) => {
        this.books = data;
      },
      error: (err) => {
        this.errMessage = err;
      },
    });
  }
}
<p>{{errMessage}}</p>
<div style="display: flex">

  <table>
    <tr>
      <td>Book Id:</td>
      <td>
        <input type="text" class="form-control" #name="ngModel" name="BookId" id="BookId" placeholder="Nhập Book Id"
          [(ngModel)]="book.BookId" />
      </td>
    </tr>
    <tr>
      <td>Book Name:</td>
      <td>
        <input type="text" class="form-control" #name="ngModel" name="BookName" id="BookName" placeholder="Nhập Book Name"
          [(ngModel)]="book.BookName" />
      </td>
    </tr>
    <tr>
      <td>Price:</td>
      <td>
        <input type="text" class="form-control" #name="ngModel" name="BookPrice" id="BookPrice" placeholder="Nhập Price"
          [(ngModel)]="book.Price" />
      </td>
    </tr>
    <tr>
      <td colspan="2">
        <button (click)="postBook()">Submit</button>
      </td>
    </tr>
  </table>

</div>
