import { Component } from '@angular/core';
import { BookAPIService } from '../services/book-api.service';

@Component({
  selector: 'app-m5-get-abook',
  templateUrl: './m5-get-abook.component.html',
  styleUrls: ['./m5-get-abook.component.css'],
})
export class M5GetAbookComponent {
  book: any;
  errMessage: string = '';
  constructor(private _service: BookAPIService) {}
  searchBook(bookID: string) {
    this._service.getBook(bookID).subscribe({
      next: (data) => {
        this.book = data;
      },
      error: (err) => {
        this.errMessage = err;
      },
    });
  }
}
<h3>Triệu gọi HTTP GET - a Book</h3>
<p>{{errMessage}}</p>
Book Id: <input type="text" #bookID> <button (click)="searchBook(bookID.value)">Search</button>
<table>
  <tr>
    <td>Book Id:</td>
    <td>{{book.bookID}}</td>
  </tr>
  <tr>
    <td>Book Name:</td>
    <td>{{book.bookName}}</td>
  </tr>
  <tr>
    <td>Price:</td>
    <td>{{book.price}} </td>
  </tr>
  <tr>
    <td colspan="2">
      <img src="assets/book_images/{{book.image}}" width="120px" height="170px">
    </td>
  </tr>
</table>
