import { Component } from '@angular/core';
import { BookAPIService } from '../services/book-api.service';

@Component({
  selector: 'app-m5-get-abook',
  templateUrl: './m5-get-abook.component.html',
  styleUrls: ['./m5-get-abook.component.css'],
})
export class M5GetAbookComponent {
  book: any;
  errMessage: string = '';
  constructor(private _service: BookAPIService) {}
  searchBook(bookID: string) {
    this._service.getBook(bookID).subscribe({
      next: (data) => {
        this.book = data;
      },
      error: (err) => {
        this.errMessage = err;
      },
    });
  }
}

<div style="margin-left: 50px;">
  <h3>Triệu gọi HTTP GET - a Book</h3>
  <p>{{errMessage}}</p>
  Search by Book ID: <input type="text" #bookID> <button (click)="searchBook(bookID.value)">Search</button>

  <div>
    <img src="assets/book_images/{{book.image}}" height="150px" >

    <div>Book ID: {{book.bookID}}</div>
    <div>Book Name: {{book.bookName}}</div>
    <div>Book Price: {{book.price}}</div>

  </div>

</div>
