import { Injectable } from '@angular/core';
import {
  HttpClient,
  HttpErrorResponse,
  HttpHeaders,
} from '@angular/common/http';
import { catchError, map, Observable, retry, throwError } from 'rxjs';

import { Ibook } from '../interfaces/books';

@Injectable({
  providedIn: 'root',
})
export class BookAPIService {
  constructor(private _http: HttpClient) {}

  getBooks(): Observable<any> {
    const headers = new HttpHeaders().set(
      'Content-Type',
      'text/plain;charset=utf- 8'
    );
    const requestOptions: Object = { headers: headers, responseType: 'text' };
    return this._http.get<any>('/books', requestOptions).pipe(
      map((res) => JSON.parse(res) as Array<Ibook>),
      retry(3),
      catchError(this.handleError)
    );
  }

  handleError(error: HttpErrorResponse) {
    return throwError(() => new Error(error.message));
  }

  getBook(BookId: string): Observable<any> {
    const headers = new HttpHeaders().set(
      'Content-Type',
      'text/plain;charset=utf-8'
    );
    const requestOptions: Object = {
      headers: headers,
      responseType: 'text',
    };
    return this._http.get<any>('/books/' + BookId, requestOptions).pipe(
      map((res) => JSON.parse(res) as Ibook),
      retry(3),
      catchError(this.handleError)
    );
  }

  sortBook(minPrice: number, maxPrice: number): Observable<any> {
    const headers = new HttpHeaders().set(
      'Content-Type',
      'text/plain;charset=utf-8'
    );
    const requestOptions: Object = {
      headers: headers,
      responseType: 'text',
    };
    return this._http
      .get<any>('/books/' + minPrice + '/' + maxPrice, requestOptions)
      .pipe(
        map((res) => JSON.parse(res) as Ibook),
        retry(3),
        catchError(this.handleError)
      );
  }

  postBook(aBook: any): Observable<any> {
    const headers = new HttpHeaders().set(
      'Content-Type', 'application/json;charset=utf-8'
    );
    const requestOptions: Object = {
      headers: headers,
      responseType: 'text',
    };
    return this._http
      .post<any>('/books', JSON.stringify(aBook), requestOptions).pipe(
        map((res) => JSON.parse(res) as Array<Ibook>),
        retry(3),
        catchError(this.handleError)
      );
  }
}
